mapper.select:
        select
           ip.cell_id,ip.supplier_brand_id,pi.product_name,ip.revshare,ip.plan_price,ip.cell_sale_volume,ip.plan_sale_volume,ip.sales,ip.views,ip.refund_orders
        from affiliate_influencer_product ip
        inner join affiliate_product_ind pi
          on pi.cell_id=ip.cell_id

        
            1=1
            AND ip.brand_id=#{dto.brandId}
            
                AND (locate(#{dto.q}, pi.supplier_brand_name)>0  or  locate(#{dto.q}, pi.product_name)>0 or  locate(#{dto.q}, pi.cell_tags)>0)
            
            
                AND TIMESTAMPDIFF(MONTH,pi.supplier_create_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S')) >= #{dto.supplierAccountAgeLeft}
            
            
                AND TIMESTAMPDIFF(MONTH,pi.supplier_create_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S')) <= #{dto.supplierAccountAgeRight}
            
            
                
                    
                        AND TIMESTAMPDIFF(WEEK,pi.supplier_modified_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S'))  <= 0
                    
                    
                        AND  TIMESTAMPDIFF(MONTH,pi.supplier_modified_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S'))  <= 0
                    
                    
                        AND  TIMESTAMPDIFF(MONTH,pi.supplier_modified_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S'))  <= 2
                    
                
            
            
                
                    
                        AND pi.supplier_blue_end_at > now()
                    
                
            
            
                AND ip.plan_price  >= #{dto.planPriceLeft}
            
            
                AND  ip.plan_price  <= #{dto.planPriceRight}
            
            
                AND ip.revshare  >= #{dto.revshareLeft}
            
            
                AND  ip.revshare  <= #{dto.revshareRight}
            
            
                AND ip.views  >= #{dto.viewsLeft} * 1000
            
            
                AND  ip.views  <= #{dto.viewsRight} * 1000
            
            
                AND  ip.plan_sale_volume  >= #{dto.saleVolumeLeft}
            
            
                AND  ip.plan_sale_volume  <= #{dto.saleVolumeRight}
            
            
                AND pi.cell_mark = #{dto.offline}
            
            
                Order by ip.create_at desc
            
            
                
                    
                        Order by ip.plan_price desc
                    
                    
                        Order by ip.revshare desc
                    
                    
                        Order by ip.plan_sale_volume desc
                    
                    
                        Order by ip.sales desc
                    
                    
                        Order by ip.views desc
                    
                    
                        Order by ip.create_at desc
