mapper.select:
        select
          pi.cell_id,pi.supplier_brand_id,pi.product_name,pi.revshare,pi.plan_price,pi.cell_sale_volume,pi.plan_sale_volume,
          pi.sales,pi.views,pi.refund_orders,pi.influencers,ifnull(pi.cell_id = inf.cell_id,0) inChoice
        from affiliate_product_ind pi
        left join affiliate_influencer_product inf
          on pi.cell_id=inf.cell_id
          AND inf.brand_id=#{dto.brandId}
        
          1=1  AND pi.cell_mark = '2'
            
                AND (locate(#{dto.q}, pi.supplier_brand_name)>0  or  locate(#{dto.q}, pi.product_name)>0)
            
            
                AND TIMESTAMPDIFF(MONTH,pi.supplier_create_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S')) >= #{dto.supplierAccountAgeLeft}
            
            
                AND TIMESTAMPDIFF(MONTH,pi.supplier_create_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S')) <= #{dto.supplierAccountAgeRight}
            
            
                
                    
                        AND TIMESTAMPDIFF(WEEK,pi.supplier_modified_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S'))  <= 1
                    
                    
                        AND  TIMESTAMPDIFF(MONTH,pi.supplier_modified_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S'))  <= 1
                    
                    
                        AND TIMESTAMPDIFF(MONTH,pi.supplier_modified_at,DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%S'))  <= 3
                    
                
            
            
                
                    
                        AND pi.supplier_blue_end_at > now()
                    
                
            
            
                 AND pi.plan_price  >= #{dto.planPriceLeft}
            
            
                AND  pi.plan_price  <= #{dto.planPriceRight}
            
            
                AND pi.provide_invoice = #{dto.provideInvoice}
            
            
                AND pi.revshare  >= #{dto.revshareLeft}
            
            
                AND  pi.revshare  <= #{dto.revshareRight}
            
            
                AND pi.views  >= #{dto.viewsLeft} * 1000
            
            
                AND  pi.views  <= #{dto.viewsRight} * 1000
            
            
                AND pi.plan_sale_volume  >= #{dto.saleVolumeLeft}
            
            
                AND  pi.plan_sale_volume  <= #{dto.saleVolumeRight}
            
            
                AND pi.influencers  >= #{dto.influencersLeft}
            
            
                AND  pi.influencers  <= #{dto.influencersRight}
