mapper.select:
select o.id orderId, buyer.brand_name buyerName, buyer.avator buyerAvatar, t.tier_name, o.total, o.status from oasis_membership_order o inner join oasis_membership_tier t on o.tier_id=t.id inner join brand buyer on o.buyer_brand_id=buyer.id where 1 = 1 and t.oasis_id = #{dto.oasisId}
AND (locate(#{dto.q}, t.tier_name)>0 or locate(#{dto.q}, buyer.brand_name)>0 or locate(#{dto.q}, o.order_no)>0)
order by o.create_at desc

select o.id orderId, o.order_no, t.tier_name, o.total, o.card_type, o.status, o.create_at, t.oasis_id, seller.brand_name sellerName, seller.avator sellerAvatar, seller.customer_id sellerUserId from oasis_membership_order o inner join oasis_membership_tier t on o.tier_id=t.id inner join brand seller on o.seller_brand_id=seller.id where 1 = 1 and o.buyer_brand_id=#{buyerBrandId}
AND (locate(#{dto.q}, t.tier_name)>0 or locate(#{dto.q}, seller.brand_name)>0 or locate(#{dto.q}, o.order_no)>0)
order by o.create_at desc
